document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".live-photo-container");e.forEach(e=>{const t=e.querySelector(".live-photo-video");t.load(),e.addEventListener("mouseenter",()=>{t.currentTime=0;const e=t.play();e!==void 0&&e.catch(e=>{console.log("Auto-play was prevented")})}),e.addEventListener("mouseleave",()=>{t.pause(),t.currentTime=0})})})